function setup() {
  background(240);
  randomSeed(424242);
  noiseSeed(424242);

  translate(width / 2, height / 2);
  rotate(map(VAR[1], 0, 100, 0, PI));

  noFill();
  stroke(20);
  strokeWeight(2);

  beginShape();
  for (let i = 0; i < 120; i++) {
    const a = map(i, 0, 119, 0, TWO_PI);
    const r = 120 + noise(cos(a) + 1, sin(a) + 1) * 60;
    vertex(cos(a) * r, sin(a) * r);
  }
  endShape(CLOSE);
}
